mapper.select:
select id, post_file_name, post_file_path, post_file_size, created_datetime, updated_datetime, post_id
from tbl_post_file
where post_id = #{postId}

select post_file_path
from tbl_post p join tbl_post_file pf
on p.id = pf.post_id and pf.id = #{id}

select id, post_file_name, post_file_path, post_file_size, created_datetime, updated_datetime, post_id
from tbl_post_file
where id = #{id}
